//Check number is positive or negative
package ifelsepack;

public class PositiveNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int m=-30;
      if(m>=0)
      {
    	  System.out.println("Number is positive " +m);
      }
      else
      {
    	  System.out.println("Number is negative " +m);
      }
      
	}

}

/* Output
  Number is negative -30
*/
